alter table SINH_VIEN add constraint SINH_VIEN_BAC_DAO_TAO foreign key (MaBac) references BAC_DAO_TAO(MaBac);
alter table SINH_VIEN add constraint SINH_VIEN_LOAI_HINH_DAO_TAO foreign key (MaLoaiHinhDaoTao) references LOAI_HINH_DAO_TAO(MaLoaiHinhDaoTao);
alter table NGANH add constraint NGANH_KHOA foreign key (MaKhoa) references KHOA(MaKhoa);
alter table LOP add constraint LOP_KHOA foreign key (MaKhoa) references KHOA(MaKhoa);
alter table LOP add constraint LOP_KHOA_HOC foreign key (MaKhoaHoc) references KHOA_HOC(MaKhoaHoc);
